targets.expr:
sum by (workflow, service_name, agent, mode) (
  irate(application_uno_aiagent_tool_seconds_count{namespace=~"$Namespace", release=~"$Release", pod=~"$pod"}[$__rate_interval])
) * 60

sum by (agent, mode) (
  irate(application_uno_aiagent_invoke_seconds_count{namespace=~"$Namespace", release=~"$Release", pod=~"$pod", status="ERROR"}[$__rate_interval])
) * 60

sum by (workflow, service_name) (
  irate(application_uno_aiagent_tool_seconds_count{namespace=~"$Namespace", release=~"$Release", pod=~"$pod", status="FAILED"}[$__rate_interval])
) * 60

sum by (agent, mode, model) (
  rate(application_uno_aiagent_invoke_seconds_sum{namespace=~"$Namespace", release=~"$Release", pod=~"$pod"}[$__rate_interval])
) / sum by (agent, mode, model) (
  rate(application_uno_aiagent_invoke_seconds_count{namespace=~"$Namespace", release=~"$Release", pod=~"$pod"}[$__rate_interval])
)

sum by (agent, mode, workflow, service_name) (
  rate(application_uno_aiagent_tool_seconds_sum{namespace=~"$Namespace", release=~"$Release", pod=~"$pod"}[$__rate_interval])
) / sum by (agent, mode, workflow, service_name) (
  rate(application_uno_aiagent_tool_seconds_count{namespace=~"$Namespace", release=~"$Release", pod=~"$pod"}[$__rate_interval])
)

round(
  avg (
    sum by (pod) (application_uno_aiagent_units_monthly_number{namespace=~"$Namespace", release=~"$Release"})
  ) +
  sum (
    increase(application_uno_genai_proxy_units_number_total{namespace=~"$Namespace", release=~"$Release"}[30d])
  )
)
